using HutongGames.PlayMaker; using UnityEngine; using UnityEngine.UI; using Charlie.Twitter;  public class ActionUploadPicture : FsmStateAction {     FsmGameObject photoGameObject;          public async override void OnEnter()     {         Texture2D texture = GameObject.Find("PhotoTaken").GetComponent<RawImage>().texture as Texture2D;         byte[] jpegData = ImageConversion.EncodeToJPG(texture, 80);          Debug.Log("[ActionUploadPicture] Uploading picture to Twitter");         Media media = await TwitterService.Instance.UploadMedia(jpegData);         Debug.Log($"[ActionUploadPicture] Media uploaded with ID {media.mediaIdString}");         TwitterService.Instance.TweetWithMedia("hello!", new string[1] { media.mediaIdString });     } } 